import React, { useState, useEffect, useRef } from 'react';


interface MainWordProps {
    words: string[];
    wordIndex: number;
    visible: boolean;
}


const MainWord: React.FC<MainWordProps> = ({words, wordIndex, visible}) => {

    const [progress, setProgress] = useState(0);
    // const [visible, setVisible] = useState(visibleStart);
    // const [wordIndex, setIndex] = useState(wordIndexStart);
    const duration = 3;
    const animationFrameHandle = useRef(0);
    const prevTime = useRef(performance.now());
    const outputValueRange = 1;

    function easeIn(x: number): number {
        return Math.pow(Math.min(1, x*2), 2);
    }

    function easeOut(x: number): number {
        return 1 - Math.pow(1 - x, 3);
    }

    useEffect(() => {
        setProgress(0);
        // if (visible) setIndex(wordIndex+2);
        console.log("Visible: "+visible+" wordIndex: "+wordIndex);
        // setVisible(!visible);
        prevTime.current = performance.now();
        function render(timestamp: DOMHighResTimeStamp) {
            animationFrameHandle.current = requestAnimationFrame(render);
            setProgress(oldValue => {
                if (oldValue >= 1) {
                    
                    cancelAnimationFrame(animationFrameHandle.current);
                    
                    return 0;
                } 
                const secondsElapsed = (timestamp - prevTime.current) / 1000;
                return Math.min(oldValue + outputValueRange * secondsElapsed / duration, 1);
            })
            prevTime.current = timestamp;
        }

        render(performance.now());
        return () => cancelAnimationFrame(animationFrameHandle.current);
    }, [JSON.stringify(words)]);
    if (visible) {
        return (
            // <div className="main-word" style={{ opacity: 1-1.2*easeIn(progress), transform: `translateX(${-100*easeIn(progress)}%)`}}>
            //   {words[wordIndex]}
            // </div>
            <div className="main-word" style={{ opacity: 1}}>
              {words[wordIndex]}
            </div>
        );
    }
    else {
        return (
            // <div className="main-word" style={{ opacity: easeOut(progress), transform: `translateX(${100-100*easeOut(progress)}%)`}}>
            //     {(wordIndex < 0)? "b": words[wordIndex-1]}
            // </div>
            <div className="main-word" style={{ opacity: 0 }}>
                {(wordIndex < 0)? "b": words[wordIndex]}
            </div>
        );
    }
  };

export default MainWord;
