import React, { useState } from 'react';

import ProgressBar from './ProgressBar'
import MainWord from './MainWord'
import Chevrons from './Chevrons'

interface WordsProps {
}


const Words: React.FC<WordsProps> = () => {


    // const [words, setCurrentWord] = useState("apple");
    // const [nextWord, setNextWord] = useState("aa");
    const [words, setWords] = useState(["apple", "banana", "cat", "dog"]);
    const [showFirst, setShowFirst] = useState(true);
    const [firstIndex, setFirstIndex] = useState(0);
    const [secondIndex, setSecondIndex] = useState(-1);
    const [counter, setCounter] = useState(0);
    const [forward, setForward] = useState(true);
    const maxWordHistory = 6;

    function getNewWord() {
        setCounter(counter+1);
        return "extra"+counter;
    }

    function forwardWord() {
        let nextFirstIndex = firstIndex;
        let nextSecondIndex = secondIndex;
        if (Math.max(secondIndex, firstIndex)+1 >= words.length) {
            const newWords = [...words];
            newWords.push(getNewWord());
            if (newWords.length > maxWordHistory) {
                newWords.shift();
                nextFirstIndex -= 1;
                nextSecondIndex -= 1;
            } 
            setWords(newWords);
        }     

        if (showFirst) nextSecondIndex += 2;
        else nextFirstIndex+=2;
        setFirstIndex(nextFirstIndex);
        setSecondIndex(nextSecondIndex);
        setShowFirst(!showFirst);
        setForward(true);
    }

    function canGoBack() {
        if (Math.min(firstIndex, secondIndex) < 0) return false;
        return true;
    }

    function previousWord() {
        if (!canGoBack()) return;
        if (showFirst && firstIndex > secondIndex) {
            console.log("First: "+firstIndex);
            console.log("Second: "+secondIndex);
        }
        else {
            if (!showFirst && secondIndex > firstIndex) {
                console.log("First: "+firstIndex);
                console.log("Second: "+secondIndex);
            }
            else {
                if (showFirst) setSecondIndex(secondIndex-2);
                else setFirstIndex(firstIndex-2);
            }
        }
        setShowFirst(!showFirst);
        setForward(false);
    }

    return (
        <div>
            <MainWord word={words[firstIndex]} visible={showFirst} onComplete={() => forwardWord()} forward={forward}></MainWord>
            <MainWord word={words[secondIndex]} visible={!showFirst} onComplete={() => forwardWord()} forward={forward}></MainWord>
            <ProgressBar onComplete={() => forwardWord()}></ProgressBar>
            <div className="chevron-button" onClick={() => previousWord()}>
                <Chevrons enabled={ canGoBack() } left={ true } ></Chevrons> 
            </div>
            <div className="chevron-button right" onClick={() => forwardWord()}>
                <Chevrons enabled={ true } left={ false } ></Chevrons> 
            </div>
        </div>
    );
};

export default Words;
