import React, { useState, useEffect, useRef } from 'react';


interface WordsDisplayProps {
    onComplete: any;
    word: string;
    visible: boolean;
    forward: boolean;
}

const WordsDisplay: React.FC<WordsDisplayProps> = ({word, visible, onComplete, forward}) => {

    const [progress, setProgress] = useState(0);
    const duration = 0.3;
    const animationFrameHandle = useRef(0);
    const prevTime = useRef(performance.now());
    const outputValueRange = 1;

    function easeIn(x: number): number {
        return 1 - Math.cos((x * Math.PI) / 2);
    }

    function easeOut(x: number): number {
        return Math.sin((x * Math.PI) / 2);;
    }

    useEffect(() => {
        setProgress(0.01);
        prevTime.current = performance.now();
        function render(timestamp: DOMHighResTimeStamp) {
            animationFrameHandle.current = requestAnimationFrame(render);
            setProgress(oldValue => {
                if (oldValue >= 1) {
                    
                    cancelAnimationFrame(animationFrameHandle.current);
                    
                    return 0;
                } 
                const secondsElapsed = (timestamp - prevTime.current) / 1000;
                return Math.max(0, Math.min(oldValue + outputValueRange * secondsElapsed / duration, 1));
            })
            prevTime.current = timestamp;
        }

        render(performance.now());
        return () => cancelAnimationFrame(animationFrameHandle.current);
    }, [onComplete]);

    let op;
    let pos;

    if (visible) {

        op = (progress === 0) ? 1 : easeOut(progress);
        if (forward) {
            pos = (progress === 0) ? 0 : 100-100*easeOut(progress)
        }
        else {
            pos = (progress === 0) ? 0 : -100+100*easeOut(progress);
        }
    }
    else {
        op = (progress === 0) ? 0 : 1-easeIn(progress)*1.3;

        if (forward) {
            pos = -100*easeIn(progress);
        }
        else {
            pos = 100*easeIn(progress);
        }
    }
    return (
        <div>
            <div className="rhymes" style={{ top: `13%`, opacity: op-.7, transform: `translateX(${pos}%)` }}>
                {word}
            </div>
            <div className="rhymes" style={{ top: `21%`,opacity: op-.57, transform: `translateX(${pos}%)` }}>
                {word}
            </div>
            <div className="rhymes" style={{ top: `29%`,opacity: op-.43, transform: `translateX(${pos}%)` }}>
                {word}
            </div>
            <div className="rhymes" style={{ top: `37%`,opacity: op-.3, transform: `translateX(${pos}%)` }}>
                {word}
            </div>
            <div className="main-word" style={{ opacity: op, transform: `translateX(${ pos }%)`}}>
                {word}
            </div>
            <div className="rhymes" style={{ top: `58%`, opacity: op-.3, transform: `translateX(${pos}%)` }}>
                {word}
            </div>
            <div className="rhymes" style={{ top: `66%`,opacity: op-.43, transform: `translateX(${pos}%)` }}>
                {word}
            </div>
            <div className="rhymes" style={{ top: `74%`,opacity: op-.57, transform: `translateX(${pos}%)` }}>
                {word}
            </div>
            <div className="rhymes" style={{ top: `82%`,opacity: op-.7, transform: `translateX(${pos}%)` }}>
                {word}
            </div>
            
        </div>
    );
  };

export default WordsDisplay;
