import React, { useState, useEffect, useRef } from 'react';

import ProgressBar from './ProgressBar'
import MainWord from './MainWord'
import { IonIcon } from '@ionic/react';

interface WordsProps {
}


const Words: React.FC<WordsProps> = () => {


    // const [words, setCurrentWord] = useState("apple");
    // const [nextWord, setNextWord] = useState("aa");
    const [words, setWords] = useState(["apple", "banana", "cat", "dog"]);
    const [showFirst, setShowFirst] = useState(true);
    const [firstIndex, setFirstIndex] = useState(0);
    const [secondIndex, setSecondIndex] = useState(-1);
    const maxWordHistory = 6;

    function getNewWord() {
        return "extra"+words.length;
    }

    function forwardWord() {
        console.log(words);
        if (Math.max(secondIndex, firstIndex)+1 >= words.length) {
            const newWords = [...words];
            newWords.push(getNewWord());
            setWords(newWords);
        }

        if (words.length > maxWordHistory) {
            words.shift();
            setFirstIndex(firstIndex-1);
            setSecondIndex(secondIndex-1);
        } 

        if (showFirst) setSecondIndex(secondIndex+2);
        else setFirstIndex(firstIndex+2);
        setShowFirst(!showFirst);
    }
    function previousWord() {
        console.log(words);
        if (Math.min(firstIndex, secondIndex) < 0) return;
        if (showFirst) setFirstIndex(firstIndex-2);
        else setSecondIndex(secondIndex-2);
        setShowFirst(!showFirst);
    }

    return (
        <div>
            <MainWord words={words} wordIndex={firstIndex} visible={showFirst}></MainWord>
            <MainWord words={words} wordIndex={secondIndex} visible={!showFirst}></MainWord>
            {/* <MainWord word={nextWord} nextWord={words} position={1}></MainWord> */}
            <ProgressBar onComplete={() => forwardWord()} words={words}></ProgressBar>
            <div className="chevron-button" onClick={() => previousWord()}>
            <IonIcon className="right chevron" src='data:image/svg+xml;utf8,<svg width="67" height="102" viewBox="0 0 67 102" fill="none" xmlns="http://www.w3.org/2000/svg">
            <rect x="50.9497" y="101.606" width="72.0537" height="22.6986" transform="rotate(-135 50.9497 101.606)" fill="white"/>
            <rect x="66.7626" y="16.0503" width="70.4294" height="22.6986" transform="rotate(135 66.7626 16.0503)" fill="white"/>
            </svg>' />
            </div>
            <div className="chevron-button right" onClick={() => forwardWord()}>
            <IonIcon className="chevron" src='data:image/svg+xml;utf8,<svg width="67" height="102" viewBox="0 0 67 102" fill="none" xmlns="http://www.w3.org/2000/svg">
                <rect x="16.0503" width="72.0537" height="22.6986" transform="rotate(45 16.0503 0)" fill="white"/>
                <rect x="0.237427" y="85.5559" width="70.4294" height="22.6986" transform="rotate(-45 0.237427 85.5559)" fill="white"/>
                </svg>' />
            </div>
        </div>
    );
};

export default Words;
